function clock(i){radius=i,lengthS=radius/10*8,lengthM=radius/10*7,lengthMI=radius/10*9,lengthH=radius/10*6;var a=new Date,e=a.getMinutes(),t=a.getHours()%12,n=a.getSeconds(),s=.36*a.getMilliseconds(),d=Math.sin(toRadians(s))*lengthMI+radius,r=Math.cos(toRadians(s))*lengthMI+radius;r=radius-(r-radius);var o=6*n,u=Math.sin(toRadians(o))*lengthS+radius,h=Math.cos(toRadians(o))*lengthS+radius;h=radius-(h-radius);var l=6*e,c=Math.sin(toRadians(l))*lengthM+radius,g=Math.cos(toRadians(l))*lengthM+radius;g=radius-(g-radius);var M=30*t+.5*e,v=Math.sin(toRadians(M))*lengthH+radius,w=Math.cos(toRadians(M))*lengthH+radius;w=radius-(w-radius);var m=document.getElementById("clock"),k=m.getContext("2d");k.canvas.width=2*radius,k.canvas.height=2*radius,k.clearRect(0,0,m.width,m.height);var R=document.getElementById("img");k.drawImage(R,0,0,2*radius,2*radius),k.beginPath(),k.moveTo(radius,radius),k.lineWidth=10,k.lineTo(v,w),k.stroke(),k.lineWidth=5,k.moveTo(radius,radius),k.lineTo(c,g),k.stroke(),k.closePath(),k.beginPath(),k.lineWidth=1,k.strokeStyle="#ff0000",k.moveTo(radius,radius),k.lineTo(u,h),k.stroke(),k.closePath(),k.beginPath(),k.strokeStyle="#000000",k.moveTo(radius,radius),k.lineTo(d,r),k.closePath(),k.stroke()}function resize(){return window.innerHeight<window.innerWidth?radius=window.innerHeight/2:radius=window.innerWidth/2,radius}function toRadians(i){return i*(Math.PI/180)}document.addEventListener("DOMContentLoaded",function(){radius=resize(),clock(radius),setInterval(function(){clock(radius)},1),window.addEventListener("resize",function(){radius=resize()})},!1);