function clock(i){radius=i,lengthS=radius/10*8,lengthM=radius/10*7,lengthMI=radius/10*9,lengthH=radius/10*6;var a=new Date,e=a.getMinutes(),t=a.getHours()%12,s=a.getSeconds(),n=a.getMilliseconds(),d=.36*n,r=Math.sin(toRadians(d))*lengthMI+radius,o=Math.cos(toRadians(d))*lengthMI+radius;o=radius-(o-radius);var u=6*s+.006*n,h=Math.sin(toRadians(u))*lengthS+radius,l=Math.cos(toRadians(u))*lengthS+radius;l=radius-(l-radius);var c=6*e+.1*s+1e-4*n,g=Math.sin(toRadians(c))*lengthM+radius,M=Math.cos(toRadians(c))*lengthM+radius;M=radius-(M-radius);var v=30*t+.5*e+s*(.5/60)+n*(.5/60/1e3),w=Math.sin(toRadians(v))*lengthH+radius,k=Math.cos(toRadians(v))*lengthH+radius;k=radius-(k-radius);var m=document.getElementById("clock"),f=m.getContext("2d");f.canvas.width=2*radius,f.canvas.height=2*radius,f.clearRect(0,0,m.width,m.height);var R=document.getElementById("img");f.drawImage(R,0,0,2*radius,2*radius),f.beginPath(),f.moveTo(radius,radius),f.lineWidth=10,f.lineTo(w,k),f.stroke(),f.lineWidth=5,f.moveTo(radius,radius),f.lineTo(g,M),f.stroke(),f.moveTo(radius,radius),f.lineTo(r,o),f.stroke(),f.closePath(),f.beginPath(),f.lineWidth=1,f.strokeStyle="#ff0000",f.moveTo(radius,radius),f.lineTo(h,l),f.stroke(),f.closePath(),f.beginPath(),f.arc(radius,radius,radius/20,0,2*Math.PI),f.fillStyle="black",f.strokeStyle="black",f.stroke(),f.fill(),f.closePath()}function resize(){return window.innerHeight<window.innerWidth?radius=window.innerHeight/2:radius=window.innerWidth/2,radius}function toRadians(i){return i*(Math.PI/180)}document.addEventListener("DOMContentLoaded",function(){radius=resize(),clock(radius),setInterval(function(){clock(radius)},1),window.addEventListener("resize",function(){radius=resize()})},!1);